</span></span><span style="display:flex;"><span>    <span style="color:#a6e22e">background-size</span><span style="color:#f92672">:</span> <span style="color:#ae81ff">57</span><span style="color:#66d9ef">px</span>;
</span></span><span style="display:flex;"><span>    <span style="color:#a6e22e">background-repeat</span><span style="color:#f92672">:</span> no-repeat;
</span></span><span style="display:flex;"><span>    <span style="color:#a6e22e">margin-bottom</span><span style="color:#f92672">:</span> <span style="color:#ae81ff">5</span><span style="color:#66d9ef">px</span>;
</span></span><span style="display:flex;"><span>    <span style="color:#a6e22e">background-position</span><span style="color:#f92672">:</span> <span style="color:#f92672">-</span><span style="color:#ae81ff">1</span><span style="color:#66d9ef">px</span> <span style="color:#ae81ff">2</span><span style="color:#66d9ef">px</span>;
