</span></span><span style="display:flex;"><span>    <span style="color:#f92672">&lt;</span><span style="color:#f92672">section</span> <span style="color:#f92672">class</span><span style="color:#f92672">=</span><span style="color:#e6db74">&#34;article-list&#34;</span><span style="color:#f92672">&gt;</span>
</span></span><span style="display:flex;"><span>        {{ <span style="color:#f92672">range</span> <span style="color:#960050;background-color:#1e0010">$</span><span style="color:#f92672">index</span><span style="color:#f92672">,</span> <span style="color:#960050;background-color:#1e0010">$</span><span style="color:#f92672">element</span> <span style="color:#a6e22e">:</span><span style="color:#f92672">=</span> <span style="color:#960050;background-color:#1e0010">$</span><span style="color:#f92672">pag</span><span style="color:#a6e22e">.Pages</span> }}
</span></span><span style="display:flex;"><span>            {{ <span style="color:#f92672">partial</span> <span style="color:#e6db74">&#34;article-list/default&#34;</span> <span style="color:#a6e22e">.</span> }}
