</span></span><span style="display:flex;"><span>            <span style="color:#1bc5e0">&#34;top&#34;</span><span style="color:#ffb8d1">:</span> y <span style="color:#ffb8d1">-</span> <span style="color:#c5a3ff">20</span>,
</span></span><span style="display:flex;"><span>            <span style="color:#1bc5e0">&#34;left&#34;</span><span style="color:#ffb8d1">:</span> x,
</span></span><span style="display:flex;"><span>            <span style="color:#1bc5e0">&#34;position&#34;</span><span style="color:#ffb8d1">:</span> <span style="color:#1bc5e0">&#34;absolute&#34;</span>,
</span></span><span style="display:flex;"><span>            <span style="color:#1bc5e0">&#34;font-weight&#34;</span><span style="color:#ffb8d1">:</span> <span style="color:#1bc5e0">&#34;bold&#34;</span>,
</span></span><span style="display:flex;"><span>            <span style="color:#1bc5e0">&#34;color&#34;</span><span style="color:#ffb8d1">:</span> <span style="color:#1bc5e0">&#34;#ff6651&#34;</span>
</span></span><span style="display:flex;"><span>        });
</span></span><span style="display:flex;"><span>        $(<span style="color:#1bc5e0">&#34;body&#34;</span>).append($i);
</span></span><span style="display:flex;"><span>        $i.animate({
</span></span><span style="display:flex;"><span>            <span style="color:#1bc5e0">&#34;top&#34;</span><span style="color:#ffb8d1">:</span> y <span style="color:#ffb8d1">-</span> <span style="color:#c5a3ff">180</span>,
</span></span><span style="display:flex;"><span>            <span style="color:#1bc5e0">&#34;opacity&#34;</span><span style="color:#ffb8d1">:</span> <span style="color:#c5a3ff">0</span>
</span></span><span style="display:flex;"><span>        },
</span></span><span style="display:flex;"><span>        <span style="color:#c5a3ff">1500</span>,
</span></span><span style="display:flex;"><span>        <span style="color:#c2ffdf">function</span>() {
</span></span><span style="display:flex;"><span>            $i.remove();
</span></span><span style="display:flex;"><span>        });
